import React, { FC } from 'react';

const saluteIcon =
    'data:image/png;base64,';
const musicIcon =
    'data:image/png;base64,';
const newsIcon =
    'data:image/png;base64,';

// По дефолту `display: none`. Включается в месте устновки
export const CarouselTouchStyles = `
.CarouselTouch {
    display: none;
    height: 38px;
    width: 38px;
}

.CarouselTouchIcon {
    width: 100%;
    position: absolute;
    top: 0;
    bottom: 0;
    border-radius: 500px;
    overflow: hidden;
}
.CarouselTouchIcon:not(:last-child) {
    box-shadow: 5px 0px 4px rgb(18 18 18 / 25%);
}
.CarouselTouchIcon__mask {
    background-color: #000000;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 2;
}
.CarouselTouchIcon__icon {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 1;
    background-size: cover;
    background-repeat: no-repeat;
}
`;

const Icon: FC<{ iconBase64: string; level: 1 | 2 | 3 }> = ({ iconBase64, level }) => (
    <div
        className="CarouselTouchIcon"
        style={{
            left: `${level === 1 ? 0 : (level - 1) * 49}%`,
            zIndex: 4 - level,
        }}
    >
        <div
            className="CarouselTouchIcon__mask"
            style={{
                opacity: level === 1 ? 0 : (level - 1) * 0.35,
            }}
        />
        <div
            className="CarouselTouchIcon__icon"
            style={{
                backgroundImage: `url(${iconBase64})`,
            }}
        />
    </div>
);

export const CarouselTouch: FC = () => (
    <div className="CarouselTouch">
        <Icon iconBase64={saluteIcon} level={1} />
        <Icon iconBase64={musicIcon} level={2} />
        <Icon iconBase64={newsIcon} level={3} />
    </div>
);
